mapper.select:
SELECT a.ifcgSeq ,a.ifcgName ,a.ifcgdeleteNY ,b.ifcddeleteNY ,b.ifcdSeq ,b.ifcdName ,b.ifcdRgstrDate ,b.ifcdModifiedDate ,b.codegroupIfcgseq FROM codegroup a join code b on a.ifcgSeq = b.codegroupIfcgseq AND b.ifcddeleteNY = #{shDelNy}
AND b.ifcdSeq = #{shValue} AND b.ifcdName LIKE CONCAT('%',#{shValue},'%')
ORDER BY b.ifcdSeq DESC LIMIT #{pagination.limitStart}, #{recordSize}

mapper.update:
update code
set 
ifcdName = #{ifcdName}
where 
ifcdSeq = #{ifcdSeq}
