mapper.select:
SELECT prSeq, prTitle, prStartDate, prEndDate, prHostedPlanned, prAgeLimit, prAttenDance, prDetails, prRgstrDate, prModifiedDate, prdeleteNY FROM performanceList WHERE 1=1 AND prdeleteNY = #{shDelNy} AND prAgeLimit = #{shAge}
AND prRgstrDate BETWEEN #{shDateStart} AND #{shDateEnd} AND prModifiedDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND prSeq = #{shValue} AND prTitle LIKE CONCAT('%',#{shValue},'%')
ORDER BY prSeq DESC

mapper.insert:

INSERT INTO performanceList
(
prTitle,
prdeleteNY,
prHostedPlanned,
prAgeLimit,
prStartDate,
prEndDate
)
VALUES
(
#{prTitle}
,#{prdeleteNY}
,#{prHostedPlanned}
,#{prAgeLimit}
,#{prStartDate}
,#{prEndDate}
)
