mapper.select:
SELECT a.prSeq, a.prTitle, a.prperformanceType, a.prViewingTime, d.plplace, a.prStartDate, a.prEndDate, a.prHostedPlanned, a.prAgeLimit, a.prAttenDance, a.prDetails, a.prRgstrDate, a.prModifiedDate, a.prdeleteNY
FROM performanceList a join placemapping b on a.prSeq = b.prSeq join place d on d.plSeq = b.plSeq WHERE 1=1
ORDER BY prSeq DESC limit #{rowNumToShow} offset #{startRnumForMysql}

SELECT a.prSeq, a.prTitle, a.prperformanceType, a.prViewingTime, d.plplace, a.prArea, a.prStartDate, a.prEndDate, a.prHostedPlanned, a.prAgeLimit, a.prAttenDance, a.prDetails, a.prRgstrDate, a.prModifiedDate, a.prdeleteNY, (select AVG(aa.rvGpa) from review aa where aa.prSeq = a.prSeq) as grade
FROM performanceList a join placemapping b on a.prSeq = b.prSeq join place d on d.plSeq = b.plSeq WHERE 1=1
ORDER BY prSeq DESC limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.update:
update seat set stReservation = 1 where
stSeq IN
#{item}

mapper.insert:
insert into selectseat
(
stSeq, 
pmSeq
)
values

	   (
	   	${item},
	   	#{pmSeq}
	   )

mapper.update:
update seat set stReservation = 0 where
stSeq IN
#{item}

mapper.insert:
INSERT INTO performanceList
(
prTitle,
prdeleteNY,
prHostedPlanned,
prAgeLimit,
prStartDate,
prEndDate
)
VALUES
(
#{prTitle}
,#{prdeleteNY}
,#{prHostedPlanned}
,#{prAgeLimit}
,#{prStartDate}
,#{prEndDate}
)
